mapper.select:
select auto_recharge_record_id id, petrol_num petrolNum, recharge_amount rechargeAmount, price price, status status, message message, recharge_time rechargeTime, order_time orderTime, create_at createAt, update_at updateAt from czb_auto_recharge_record
petrol_num = #{petrolNum}
AND ( order_time BETWEEN #{startTime} AND #{endTime} )
AND status = #{status,jdbcType=INTEGER}
ORDER BY update_at DESC
