mapper.select:
select commodity_id as commodityId, shop_id as shopId, commodity_title as commodityTitle, commodity_info as commodityInfo, discount as discount, save_path as commodityImg,commodity_price as commodityPrice, classification as classification, usage_count as usageCount, czb_commodity.create_at as createAt, czb_commodity.update_at as updateAt from czb_commodity,czb_file
classification=#{classification,jdbcType=VARCHAR}
and commodity_img=file_id

select distinct classification as navName
from czb_commodity


    classification=#{classification,jdbcType=VARCHAR}

SELECT commodity_id AS commodityId, shop_id AS shopId, commodity_title AS commodityTitle, commodity_info AS commodityInfo, discount AS discount, commodity_img AS commodityImg, b.save_path AS savePath, commodity_price AS commodityPrice, classification AS classification, usage_count AS usageCount, a.create_at AS createAt, a.update_at AS updateAt FROM czb_commodity LEFT JOIN czb_file b ON a.commodity_img = b.file_id WHERE shop_id = #{shopId,jdbcType=VARCHAR}
AND a.create_at >= #{startTime,jdbcType=TIMESTAMP}
AND a.create_at <= #{endTime,jdbcType=TIMESTAMP}
AND #{startTime,jdbcType=TIMESTAMP} <=a.create_at <= #{endTime,jdbcType=TIMESTAMP}
AND commodity_price = #{commodityPrice,jdbcType=DOUBLE}
