mapper.select:
select
enterprise_info_id, enterprise_name, legal_person, contact_info,czb_enterprise_info.is_deleted, user_account,
org_code, czb_enterprise_info.create_at, czb_enterprise_info.update_at,COUNT(enterprise_info_id) as counts
from czb_enterprise_info
LEFT JOIN czb_user ON czb_enterprise_info.user_id = czb_user.user_id
LEFT JOIN czb_contract_records on czb_enterprise_info.user_id = czb_contract_records.user_id

    czb_enterprise_info.is_deleted = 0

       AND enterprise_info_id = #{enterpriseInfo.enterpriseInfoId,jdbcType=VARCHAR}


        AND enterprise_name= #{enterpriseInfo.enterpriseName,jdbcType=VARCHAR}


        AND  user_account= #{enterpriseInfo.userAccount,jdbcType=VARCHAR}


        AND contact_info=#{enterpriseInfo.contactInfo,jdbcType=VARCHAR}


        AND create_at >= #{enterpriseInfo.startTime,jdbcType=TIMESTAMP}


        AND  #{enterpriseInfo.endTime,jdbcType=TIMESTAMP} >=create_at


        AND   #{enterpriseInfo.endTime,jdbcType=TIMESTAMP}>= create_at
        AND create_at>= #{enterpriseInfo.startTime,jdbcType=TIMESTAMP}


GROUP BY enterprise_info_id

mapper.update:
update czb_enterprise_info
set is_deleted = 1
where enterprise_info_id = #{enterpriseInfoId,jdbcType=VARCHAR}
