mapper.select:
select stock_id
from czb_h5_stock
where state = 0
and commodity_id = #{commodityId,jdbcType=VARCHAR}
and price = #{price,jdbcType=DOUBLE}
limit 1

select user_id
from czb_h5_stock
where stock_id = #{stockId,jdbcType=VARCHAR}

mapper.update:
update czb_h5_stock
set user_id = #{userId,jdbcType=VARCHAR},
    state = 1,
    recharge_amount = #{payPrice}
where stock_id = #{stockId,jdbcType=VARCHAR}
