mapper.select:
SELECT czb_user.user_id as userId, czb_user.user_account as userAccount, partner, record_id as recordId, target_promotion_number as targetPromotionNumber, actual_promotion_number as actualPromotionNumber, target_new_consumer as targetNewConsumer, actual_new_consumer as actualNewConsumer, state, indicator_begin_time as indicatorBeginTime, indicator_end_time as indicatorEndTime, mission_start_time as missionStartTime, mission_end_time as missionEndTime FROM czb_user LEFT JOIN czb_indicator_record ON czb_user.user_id = czb_indicator_record.user_id WHERE czb_indicator_record.user_id IS NOT NULL
AND user_account like concat('%',#{indicatorRecordDTO.userAccount,jdbcType = VARCHAR},'%')
AND state = #{indicatorRecordDTO.state,jdbcType=INTEGER}
AND partner = #{indicatorRecordDTO.partner,jdbcType = INTEGER}
AND indicator_begin_time >= #{indicatorRecordDTO.indicatorBeginTime,jdbcType=TIMESTAMP}
AND indicator_end_time <= #{indicatorRecordDTO.indicatorEndTime,jdbcType=TIMESTAMP}
AND record_id in
${indicatorRecordDTO.recordIds}
