mapper.select:
SELECT czb_user.user_id as userId, czb_user.user_account as userAccount, partner, record_id as recordId, target_promotion_number as targetPromotionNumber, actual_promotion_number as actualPromotionNumber, target_new_consumer as targetNewConsumer, actual_new_consumer as actualNewConsumer, state, indicator_begin_time as indicatorBeginTime, indicator_end_time as indicatorEndTime FROM czb_user LEFT JOIN czb_indicator_record ON czb_user.user_id = czb_indicator_record.user_id WHERE czb_indicator_record.user_id IS NOT NULL
AND user_account = #{userAccount,jdbcType = VARCHAR}
AND state = #{state,jdbcType=INTEGER}
AND partner = #{partner,jdbcType = INTEGER}
AND indicator_begin_time >= #{indicatorBeginTime,jdbcType=TIMESTAMP}
AND indicator_end_time <= #{indicatorEndTime,jdbcType=TIMESTAMP}
