mapper.select:
SELECT msg_model_id messageModelId, msg_title messageTitle, msg_content messageContent, czb_msg_model.create_at messageSendTime, czb_msg_model.altert 'alter', czb_msg_model.receiver_type receiverType, is_send isSend FROM czb_msg_model WHERE 1 = 1
AND msg_title LIKE concat('%',#{messageListDTO.messageTitle,jdbcType = VARCHAR},'%')
AND czb_msg_model.create_at >= #{messageListDTO.messageStartTime, jdbcType = TIMESTAMP}
AND czb_msg_model.create_at <= #{messageListDTO.messageEndTime, jdbcType = TIMESTAMP}
AND receiver_type = #{messageListDTO.receicerType, jdbcType = INTEGER}
AND is_send = #{messageListDTO.isSend, jdbcType = INTEGER}
