mapper.select:
SELECT t2.user_account 'owner', t1.transaction_time transactionTime, t1.payment_method paymentMethod, t1.turnover_amount turnoverAmount, t3.area area, t3.petrol_price petrolPrice, t3.petrol_denomination petrolDenomination, t3.petrol_kind petrolKind, t3.petrol_num petrolNum
FROM ( SELECT * FROM czb_petrol_sales_records AS records
and records.payment_method = #{paymentMethod,jdbcType=INTEGER}
and records.transaction_time = #{transactionTime,jdbcType=TIMESTAMP}
) AS t1 INNER JOIN ( SELECT _user.user_id, _user.user_account FROM czb_user AS _user
and _user.user_account = #{owner,jdbcType = VARCHAR}
) AS t2 ON t1.buyer_id = t2.user_id INNER JOIN ( SELECT * FROM czb_petrol AS petrol
AND petrol.area = #{area,jdbcType=VARCHAR}
AND petrol.petrol_num = #{petrolNum,jdbcType=VARCHAR}
AND petrol.petrol_price = #{petrolPrice,jdbcType = DOUBLE}
AND petrol.petrol_denomination = #{petrolDenomination,jdbcType = DOUBLE}
and petrol.petrol_kind = #{petrolKind,jdbcType = VARCHAR}
) AS t3 ON t3.petrol_id = t1.petrol_id
