mapper.select:
SELECT t2.user_account 'owner', t1.transaction_time transactionTime, t1.payment_method paymentMethod, t1.turnover_amount turnoverAmount, t3.area area, t3.petrol_price petrolPrice, t3.petrol_denomination petrolDenomination, t3.petrol_kind petrolKind, t3.petrol_num petrolNum
FROM ( SELECT * FROM czb_petrol_sales_records AS records
and records.payment_method like concat('%',#{paymentMethod,jdbcType=INTEGER},'%')
and records.transaction_time like concat('%',DATE_FORMAT(#{transactionTime,jdbcType=TIMESTAMP},'%Y-%m-%d') ,'%')
) AS t1 INNER JOIN ( SELECT _user.user_id, _user.user_account FROM czb_user AS _user
and _user.user_account like concat('%',#{owner,jdbcType = VARCHAR},'%')
) AS t2 ON t1.buyer_id = t2.user_id INNER JOIN ( SELECT * FROM czb_petrol AS petrol
AND petrol.area like concat('%',#{area,jdbcType=VARCHAR},'%')
AND petrol.petrol_num like concat('%',#{petrolNum,jdbcType=VARCHAR},'%')
AND petrol.petrol_price =#{petrolPrice,jdbcType = DOUBLE}
AND petrol.petrol_denomination = #{petrolDenomination,jdbcType = DOUBLE}
and petrol.petrol_kind = #{petrolKind,jdbcType = VARCHAR}
) AS t3 ON t3.petrol_id = t1.petrol_id order by t1.transaction_time desc

SELECT
FROM ( SELECT record_id, petrol_num, petrol_id, buyer_id, is_recharged, create_at, update_at, payment_method, petrol_kind FROM czb_petrol_sales_records
and petrol_num LIKE CONCAT(CONCAT('%',#{petrolNum,jdbcType = VARCHAR}),'%')
and is_recharged = #{isRecharged,jdbcType = INTEGER}
and petrol_kind = #{petrolKind,jdbcType = INTEGER}
and is_recharged != -1
) AS t_cpsr INNER JOIN ( SELECT cu.user_account, cu.user_name, cu.user_id FROM czb_user AS cu WHERE cu.user_id IN ( SELECT buyer_id FROM czb_petrol_sales_records ) ) AS t_user ON t_user.user_id = t_cpsr.buyer_id INNER JOIN ( SELECT petrol_id, petrol_denomination, petrol_price FROM czb_petrol ) AS cp ON cp.petrol_id = t_cpsr.petrol_id order by create_at desc
