mapper.update:
update czb_shop set
audit = #{audit,jdbcType=INTEGER},
update_at = now() where shop_id = #{shopId,jdbcType=VARCHAR}

mapper.insert:
  insert into czb_shop (shop_id, user_id, shop_name,
  shop_phone, shop_content, shop_address,
  create_at, update_at, audit
  )
  values (#{shopId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{shopName,jdbcType=VARCHAR},
  #{shopPhone,jdbcType=VARCHAR}, #{shopContent,jdbcType=VARCHAR}, #{shopAddress,jdbcType=VARCHAR},
now(),now(), #{audit,jdbcType=INTEGER}
  )

mapper.select:
SELECT czb_order.id orderId, czb_order.shop_id shopId, czb_commodity.commodity_title commodityTitle, actual_price actualPrice, pay_method payMethod, third_order thirdOrder, czb_order.create_at createAt FROM czb_order LEFT JOIN czb_commodity ON czb_order.commodity_id = czb_commodity.commodity_id WHERE czb_order.state = 1 AND czb_order.is_settlement = 0
AND czb_order.shop_id = #{shopId, jdbcType = VARCHAR}
AND czb_order.create_at >= #{startTime, jdbcType = TIMESTAMP}
AND czb_order.create_at <= #{endTime, jdbcType = TIMESTAMP}
AND czb_commodity.commodity_title LIKE concat('%', #{commodityTitle, jdbcType = VARCHAR} ,'%')
AND czb_order.id LIKE concat('%', #{orderId, jdbcType = VARCHAR} ,'%')
