SELECT record_id recordId, czb_vip_recharge_records.user_id userId, amount, recharge_way rechargeWay, is_received isReceived, third_trade_num thirdTradeNum, area, recharge_time rechargeTime, user_account userAccount FROM czb_vip_recharge_records LEFT JOIN czb_user ON czb_user.user_id = czb_vip_recharge_records.user_id LEFT JOIN czb_vip_area_config ON czb_vip_recharge_records.vip_area_config_id = czb_vip_area_config.vip_area_config_id WHERE is_received = 1
and is_special = #{isSpecial,jdbcType = INTEGER}
AND recharge_time >= #{startTime,jdbcType = TIMESTAMP}
AND recharge_time <= #{endTime,jdbcType = TIMESTAMP}
AND area LIKE concat('%',#{area,jdbcType = VARCHAR},'%')
AND czb_user.user_account LIKE concat('%',#{userAccount,jdbcType = VARCHAR},'%')
AND recharge_way = #{rechargeWay,jdbcType = INTEGER}
ORDER BY czb_vip_recharge_records.create_at desc

SELECT SUM(amount) FROM czb_vip_recharge_records LEFT JOIN czb_user ON czb_user.user_id = czb_vip_recharge_records.user_id LEFT JOIN czb_vip_area_config ON czb_vip_recharge_records.vip_area_config_id = czb_vip_area_config.vip_area_config_id WHERE is_received = 1
and czb_user.is_special = #{isSpecial,jdbcType = INTEGER}
AND recharge_time >= #{startTime,jdbcType = TIMESTAMP}
AND recharge_time <= #{endTime,jdbcType = TIMESTAMP}
AND area LIKE concat('%',#{area,jdbcType = VARCHAR},'%')
AND czb_user.user_account LIKE concat('%',#{userAccount,jdbcType = VARCHAR},'%')
AND recharge_way = #{rechargeWay,jdbcType = INTEGER}

SELECT record_id recordId, czb_vip_recharge_records.user_id userId, czb_user.superior_user superiorUser, amount, recharge_way rechargeWay, is_received isReceived, third_trade_num thirdTradeNum, area, recharge_time rechargeTime, user_account userAccount FROM czb_vip_recharge_records LEFT JOIN czb_user ON czb_user.user_id = czb_vip_recharge_records.user_id LEFT JOIN czb_vip_area_config ON czb_vip_recharge_records.vip_area_config_id = czb_vip_area_config.vip_area_config_id WHERE is_received = 1
and czb_user.is_special = #{isSpecial,jdbcType = INTEGER}
AND recharge_time >= #{startTime,jdbcType = TIMESTAMP}
AND recharge_time <= #{endTime,jdbcType = TIMESTAMP}
AND area LIKE concat('%',#{area,jdbcType = VARCHAR},'%')
AND czb_user.user_account LIKE concat('%',#{userAccount,jdbcType = VARCHAR},'%')
AND recharge_way = #{rechargeWay,jdbcType = INTEGER}
ORDER BY czb_vip_recharge_records.create_at desc

select COUNT(user_id) as totalOrder,
     SUM(amount) as totalAmount
from czb_vip_recharge_records
WHERE
is_received = 1

  AND recharge_time >= #{startTime,jdbcType = TIMESTAMP}


  AND recharge_time <= #{endTime,jdbcType = TIMESTAMP}


    and recharge_way = #{rechargeWay,jdbcType = INTEGER}
