mapper.select:
SELECT t2.user_account userAccount, t1.recharge_account rechargeAccount, t1.recharge_amount rechargeAmount, t1.record_type recordType, t1.create_at createAt FROM czb_direct_charging_order t1 LEFT JOIN czb_user t2 ON t1.user_id = t2.user_id WHERE 1 = 1
AND t1.user_id = #{userId}
AND t1.record_type = #{recordType}
AND t1.create_at >= #{startTime} AND t1.create_at <= #{endTime}
AND t1.recharge_account LIKE CONCAT('%',#{rechargeAccount},'%')
ORDER BY t1.create_at DESC

SELECT SUM(recharge_amount)
FROM czb_direct_charging_order
WHERE user_id = #{userId}

    AND record_type = #{recordType}


    AND create_at >= #{startTime}
    AND create_at <= #{endTime}


    AND recharge_account LIKE CONCAT('%',#{rechargeAccount},'%')

mapper.delete:
delete from czb_user_role
where user_id = #{userId}
  and role_id = '45861973747620085'
