mapper.select:
SELECT
    user_account userAccount,
    sinopec_petrol_num sinopecPetrolNum,
    petrolChina_petrol_num petrolChinaPetrolNum,
    a.create_at createAt
FROM czb_user_card_relation a left join czb_user b on a.user_id = b.user_id


        b.user_account = #{userAccount,jdbcType=VARCHAR}


        and a.create_at >= #{startTime,jdbcType=TIMESTAMP}
        and a.create_at <= #{endTime,jdbcType=TIMESTAMP}

SELECT a.order_id orderId, a.third_order_id thirdOrderId, a.recharge_amount rechargeAmount, a.real_price realPrice, a.state state, a.create_at createAt, b.user_account userAccount, cu.user_account superiorAccount, d.sinopec_petrol_num sinopecPetrolNum, d.petrolChina_petrol_num petrolChinaPetrolNum FROM czb_direct_charging_order a left join czb_user b on a.user_id = b.user_id left join czb_user cu on cu.user_id = b.superior_user left join czb_user_card_relation d on a.user_id = d.user_id
a.record_type = 1
a.record_type = 2 or a.record_type = 3
and a.create_at >= #{startTime,jdbcType=TIMESTAMP} and a.create_at <= #{endTime,jdbcType=TIMESTAMP}
and a.recharge_amount = #{rechargeAmount,jdbcType=VARCHAR}
and b.user_account = #{userAccount,jdbcType=VARCHAR}
and b.sinopec_petrol_num = #{sinopecPetrolNum,jdbcType=VARCHAR}
and b.petrolChina_petrol_num = #{petrolChinaPetrolNum,jdbcType=VARCHAR}
and cu.user_account = #{superiorAccount,jdbcType=VARCHAR}
order by a.create_at DESC

SELECT order_id orderId, third_order_id thirdOrderId, recharge_amount rechargeAmount, real_price realPrice, state state, a.create_at createAt, recharge_account rechargeAccount, record_type recordType, customer_number customerNumber, b.user_account theUserAccount, cardholder cardholder FROM czb_direct_charging_order a left join czb_user b on a.user_id = b.user_id
`state` != 0
and (record_type = 1 or record_type = 5)
and (record_type = 2 or record_type = 3 or record_type = 4)
and a.create_at >= #{startTime,jdbcType=TIMESTAMP} and a.create_at <= #{endTime,jdbcType=TIMESTAMP}
and recharge_account = #{rechargeAccount,jdbcType=VARCHAR}
and customer_number = #{customerNumber,jdbcType=VARCHAR}
and `state` = #{state,jdbcType=INTEGER}
and cardholder like CONCAT('%',#{cardholder,jdbcType=VARCHAR},'%')
order by a.create_at DESC

SELECT order_id orderId, third_order_id thirdOrderId, recharge_amount rechargeAmount, real_price realPrice, state state, a.create_at createAt, recharge_account rechargeAccount, record_type recordType, customer_number customerNumber, b.user_account theUserAccount, cardholder cardholder FROM czb_direct_charging_order a left join czb_user b on a.user_id = b.user_id
`state` != 0
and record_type = 1
and (record_type = 2 or record_type = 3 or record_type = 4)
and a.create_at >= #{startTime,jdbcType=TIMESTAMP} and a.create_at <= #{endTime,jdbcType=TIMESTAMP}
and recharge_account = #{rechargeAccount,jdbcType=VARCHAR}
and customer_number = #{customerNumber,jdbcType=VARCHAR}
and `state` = #{state,jdbcType=INTEGER}
and cardholder like CONCAT('%',#{cardholder,jdbcType=VARCHAR},'%')
order by a.create_at DESC limit 0,5

SELECT
sum(recharge_amount) totalRechargeAmount,
sum(real_price) totalRealPrice
FROM czb_direct_charging_order

    `state` != 0

        and record_type = 1


        and (record_type = 2 or record_type = 3 or record_type = 4)


        and create_at >= #{startTime,jdbcType=TIMESTAMP}
        and create_at <= #{endTime,jdbcType=TIMESTAMP}


        and recharge_account = #{rechargeAccount,jdbcType=VARCHAR}


        and customer_number = #{customerNumber,jdbcType=VARCHAR}


        and `state` = #{state,jdbcType=INTEGER}


        and cardholder like CONCAT('%',#{cardholder,jdbcType=VARCHAR},'%')

SELECT COUNT(record_id)
FROM czb_user_card_relation


        petrolChina_petrol_num = #{pertrolNum,jdbcType=VARCHAR}


        sinopec_petrol_num = #{pertrolNum,jdbcType=VARCHAR}

SELECT COUNT(record_id)
FROM czb_user_card_relation
WHERE user_id = #{userId,jdbcType=VARCHAR}

