mapper.select:
SELECT commodity_id AS commodityId, account AS account, discount AS discount, preferential_price AS preferentialPrice, commodity_type as commodityType, state AS state, create_at AS createAt, update_at AS updateAt FROM czb_direct_charging_commodity
commodity_type = #{_parameter,jdbcType=INTEGER}
and state = 1

SELECT commodity_id AS commodityId, account AS account, discount AS discount, preferential_price AS preferentialPrice, commodity_type as commodityType, state AS state, create_at AS createAt, update_at AS updateAt FROM czb_direct_charging_commodity
commodity_type = #{commodityType,jdbcType=INTEGER}
and state = #{state,jdbcType=INTEGER}
order by update_at desc

mapper.update:
update czb_direct_charging_commodity
account = #{account,jdbcType=DOUBLE}, discount = #{discount,jdbcType=DOUBLE}, preferential_price = #{preferentialPrice,jdbcType=DOUBLE}, commodity_type = #{commodityType,jdbcType=INTEGER}, state = #{state,jdbcType=INTEGER}, update_at = now();
where commodity_id = #{commodityId,jdbcType=VARCHAR}
