mapper.select:
select customer_id as customerId, app_id as appId, app_secret as appSecret, customer_number as customerNumber, recharge_amount as rechargeAmount, balance, consumption_amount as consumptionAmount, amount_recovered as amountRecovered, create_at as createAt, customer_name as customerName from czb_direct_customer
customer_number like CONCAT('%',#{customerNumber,jdbcType=VARCHAR},'%')
and create_at >= #{startTime,jdbcType=TIMESTAMP} and create_at <= #{endTime,jdbcType=TIMESTAMP}

mapper.update:
update czb_direct_customer
set
recharge_amount = #{rechargeAmount},
balance = #{balance}
where
customer_id = #{customerId}

update czb_direct_customer
set
amount_recovered = #{amountRecovered},
balance = #{balance}
where
customer_id = #{customerId}
