mapper.select:
select t1.goods_id, t1.goods_title, t1.type_id, t2.type_name, t1.selling_price, t1.unit_price, t1.current_price, t1.fy_mount, t1.sold_number, t1.on_sale, t3.category_id, t3.category_name, t4.save_path AS goods_pic from czb_equity_payment_commodity t1 left join czb_equity_payment_type t2 on t1.type_id = t2.type_id left join czb_equity_payment_category t3 on t2.category_id = t3.category_id left join czb_file t4 on t1.goods_pic = t4.file_id where t1.is_delete = 0
and t1.type_id = #{typeId, jdbcType = VARCHAR}
and t1.goods_title like concat("%",#{goodsTitle, jdbcType = VARCHAR}, "%")
and t3.category_id = #{categoryId, jdbcType = VARCHAR}
and t1.is_hot = #{isHot, jdbcType = INTEGER}
order by sold_number desc

mapper.update:
update czb_equity_payment_commodity
set is_delete = #{isDelete,jdbcType=INTEGER}
where goods_id = #{goodsId,jdbcType=VARCHAR}
