mapper.select:
SELECT t1.type_id typeId, t1.type_name typeName, t1.pic pic, t1.is_hot typeIsHot, t2.category_id categoryId, t2.category_name categoryName, t3.save_path AS src FROM czb_equity_payment_type t1 LEFT JOIN czb_equity_payment_category t2 ON t1.category_id = t2.category_id LEFT JOIN czb_file t3 ON t3.file_id = t1.pic WHERE t1.is_delete = 0
AND t1.category_id = #{categoryId, jdbcType = VARCHAR}
ORDER BY t1.order
