mapper.select:
SELECT dish_id, shop_id, dish_name, dish_type, supply_time, is_sale, label, dish_content, is_recommend, original_price, file.save_path, current_price, vip_price, dish.file_id, dish.create_at, dish.update_at from czb_dish dish left join czb_file file on file.file_id = dish.file_id
AND dish_id = #{dishId}
AND dish_name like concat("%", #{dishName}, "%")
AND dish_type = #{dishType}
AND shop_id = #{shopId}

mapper.delete:
delete from czb_dish_relationship
where dish_relationship_id in

  #{item.relationId}

mapper.select:
SELECT shop_id
from czb_shop
where user_id = #{userId}
