mapper.select:
select czb_h5_stock.stock_id stockId, czb_h5_stock.commodity_id commodityId, commodity_title commodityTitle, czb_h5_stock.content, czb_h5_stock.state, czb_h5_stock.create_at createAt, czb_h5_stock.update_at updateAt, czb_h5_stock.record_type recordType, czb_h5_stock.user_id userId, czb_h5_stock.price, czb_h5_stock.third_order thirdOrder from czb_h5_stock join czb_h5_commodity on czb_h5_stock.commodity_id = czb_h5_commodity.commodity_id
czb_h5_commodity.commodity_id = #{commodityId}
and commodity_title = #{commodityTitle}
and czb_h5_stock.state = #{state}
and czb_h5_stock.create_at > #{startTime} and czb_h5_stock.create_at < #{endTime}

select czb_h5_stock.stock_id stockId, czb_h5_stock.commodity_id commodityId, commodity_title commodityTitle, czb_h5_stock.content, czb_h5_stock.state, czb_h5_stock.create_at createAt, czb_h5_stock.update_at updateAt, czb_h5_stock.record_type recordType, czb_h5_stock.user_id userId, czb_h5_stock.price, czb_h5_stock.recharge_amount rechargeAmount, czb_user.user_account userAccount, czb_h5_stock.third_order thirdOrder from czb_h5_stock join czb_h5_commodity on czb_h5_stock.commodity_id = czb_h5_commodity.commodity_id join czb_user on czb_h5_stock.user_id = czb_user.user_id
czb_h5_stock.state = 2
and czb_h5_commodity.commodity_id = #{commodityId}
and czb_h5_stock.record_type = #{recordType}
and czb_h5_stock.content like concat('%',#{content},'%')
and czb_user.user_account = #{userAccount}
and commodity_title = #{commodityTitle}
and czb_h5_stock.update_at > #{startTime} and czb_h5_stock.update_at < #{endTime}
order by czb_h5_stock.update_at DESC

select commodity_id commodityId, commodity_title commodityTitle, is_delete isDelete, create_at createAt, update_at updateAt from czb_h5_commodity
1=1
and commodity_title = #{commodityTitle}
and create_at > #{startTime} and create_at < #{endTime}

select ifnull(SUM(recharge_amount), 0) from czb_h5_stock join czb_user on czb_h5_stock.user_id = czb_user.user_id where 1 = 1 and `state` = 2
and record_type = #{recordType}
and content like concat('%',#{content},'%')
and czb_user.user_account = #{userAccount}
and (czb_h5_stock.update_at >= #{startTime, jdbcType = VARCHAR} and czb_h5_stock.update_at <= #{endTime, jdbcType = VARCHAR})
