mapper.select:
SELECT b.user_account AS userAccount, a.current_total AS currentTotal, a.got_total AS gotTotal, a.update_at AS updateAt FROM czb_integral_info a JOIN czb_user b ON a.user_id = b.user_id
WHERE b.user_account = #{userAccount,jdbcType=VARCHAR}
ORDER BY a.update_at DESC

select got_total gotTotal
from czb_integral_info
where user_id = #{userId, jdbcType=VARCHAR}
