mapper.select:
select

FROM czb_server_coupon a
LEFT JOIN czb_coupon_standard b ON a.coupon_standard = b.standard_id
LEFT JOIN czb_user c ON  a.owner_id = c.user_id


        owner_id = #{ownerId,jdbcType=VARCHAR}


       AND status = #{status,jdbcType=TINYINT}


        AND coupon_standard = #{standardType,jdbcType=VARCHAR}


        AND user_account LIKE CONCAT('%',#{userAccount,jdbcType=VARCHAR},'%' )


        AND destroy_time <= #{destroyTime,jdbcType=TIMESTAMP}

select coupon_id as couponId,destroy_time as destroyTime,standard_type as standardType, standard_value as standardValue,standard_explain as standardExplain ,standard_id as couponStandardId, coupon_name as couponName from czb_server_coupon,czb_coupon_standard
owner_id = #{userId,jdbcType=VARCHAR} and standard_id=coupon_standard and is_delete=0 AND destroy_time >= now() and status=0
and coupon_id=#{couponId,jdbcType=VARCHAR}
