mapper.select:
select
from czb_vehicle_clean_order LEFT JOIN czb_user ON czb_vehicle_clean_order.user_id = czb_user.user_id LEFT JOIN czb_clean_server_vehicle ON czb_vehicle_clean_order.vehicle_id = czb_clean_server_vehicle.vehicle_id LEFT JOIN czb_clean_rider ON czb_vehicle_clean_order.rider_id = czb_clean_rider.rider_id where czb_vehicle_clean_order.user_id = #{userId,jdbcType=VARCHAR}
AND server_order_id = #{serverOrderId,jdbcType=VARCHAR}

mapper.update:
update czb_vehicle_clean_order
set
pay_status = #{payStatus,jdbcType=TINYINT},
process_status = #{processStatus,jdbcType=TINYINT},
update_at = #{updateAt,jdbcType=TIMESTAMP}
where server_order_id = #{serverOrderId,jdbcType=VARCHAR}

mapper.select:
SELECT orders.server_order_id, servers.server_name, vehicles.user_name , vehicles.license_number , vehicles.vehicle_type , vehicles.vehicle_series , vehicles.vehicle_color , riders.rider_name , riders.contact_number , orders.pay_status , orders.process_status , orders.actual_price , orders.cancel_time , orders.cancel_reason , orders.create_at FROM czb_vehicle_clean_order orders LEFT JOIN czb_server_standard servers ON servers.server_id = orders.server_id LEFT JOIN czb_clean_server_vehicle vehicles ON vehicles.vehicle_id = orders.vehicle_id LEFT JOIN czb_clean_rider riders ON riders.rider_id = orders.rider_id
server_order_id = #{serverOrderId}
AND vehicles.license_number = #{carNum}
AND vehicles.user_name = #{firstName}
AND riders.contact_number like concat('%', #{riderPhone}, '%')
AND riders.rider_name = #{riderName}
