mapper.select:
select commodity_id, czb_shop.shop_id, commodity_title, commodity_info, discount, commodity_img_id, cost_price, original_price, sale_price, commmodity_type_id, czb_wechat_commodity.starting_time_business, czb_wechat_commodity.end_time_business, commodity_source, commodity_num, is_sale, take_way, czb_shop.shop_name, czb_wechat_commodity.shop_phone, czb_wechat_commodity.shop_content, czb_wechat_commodity.shop_address, czb_wechat_commodity.longitude, czb_wechat_commodity.latitude, is_have_shop, show_place, show_order, czb_wechat_commodity.create_at, czb_wechat_commodity.update_at, czb_wechat_commodity.fy_money fyMoney from czb_wechat_commodity LEFT JOIN czb_shop ON czb_wechat_commodity.shop_id = czb_shop.shop_id
AND commodity_title LIKE concat('%',#{commodityTitle,jdbcType=VARCHAR},'%')
AND show_place = #{showPlace,jdbcType=VARCHAR}
AND is_sale = #{isSale,jdbcType=INTEGER}
AND commodity_source LIKE concat('%',#{commoditySource,jdbcType=VARCHAR},'%')
AND take_way LIKE concat('%',#{takeWay,jdbcType=VARCHAR},'%')
AND czb_shop.user_id = #{userId,jdbcType=INTEGER}

SELECT czb_wechat_commodity.commodity_id commodityId, commodity_title commodityTitle, commodity_info commodityInfo, original_price originalPrice, sale_price salePrice, take_way takeWay, commodity_img_id commodityImgId, czb_shop.shop_name shopName, czb_shop.shop_content shopContent, czb_shop.shop_address shopAddress, czb_wechat_commodity.commodity_num commodityNum, czb_shop.longitude, czb_shop.latitude, is_have_shop isHaveShop, czb_shop.shop_phone shopPhone, czb_wechat_commodity.fy_money fyMoney, czb_wechat_commodity.starting_time_business startingTimeBusiness, czb_wechat_commodity.end_time_business endTimeBusiness, czb_wechat_commodity.commodity_introduce commodityIntroduce, IFNULL( SUM( czb_wechat_commodity_order.commodity_num ), 0 ) salesVolume FROM czb_wechat_commodity czb_wechat_commodity LEFT JOIN czb_wechat_commodity_order ON czb_wechat_commodity.commodity_id = czb_wechat_commodity_order.commodity_id LEFT JOIN czb_shop ON czb_shop.shop_id = czb_wechat_commodity.shop_id WHERE czb_wechat_commodity.is_sale = 1 AND ( pay_status = 1 OR pay_status IS NULL ) AND czb_wechat_commodity.end_time_business >= now()
AND area = #{area, jdbcType = VARCHAR}
AND show_place = #{shopPlace, jdbcType = VARCHAR}
GROUP BY commodityId ORDER BY show_order

mapper.update:
update czb_wechat_commodity
SET is_sale = #{isSale, jdbcType = INTEGER}
WHERE czb_wechat_commodity.commodity_id in (${ids})
