mapper.select:
SELECT user_account as userAccount, order_id as orderId, third_order as thirdOrder, order_state as orderState, order_table.commodity_id as commodityId, commodity_table.commodity_title as commodityTitle, order_table.commodity_num as commodityNum, order_table.actual_price as actualPrice, pay_status as payStatus, pay_method as payMethod, order_table.create_at as createAt FROM czb_wechat_commodity_order as order_table LEFT JOIN czb_wechat_commodity as commodity_table ON order_table.commodity_id = commodity_table.commodity_id LEFT JOIN czb_user as user_table ON user_table.user_id = order_table.user_id
order_table.order_id IS NOT NULL
AND user_table.user_account like concat('%', #{userAccount}, '%')
AND order_table.order_id like concat('%', #{orderId}, '%')
AND order_table.third_order like concat('%', #{thirdOrder}, '%')
AND order_state = #{orderState}
AND pay_status = #{payStatus}
AND commodity_table.commodity_title like concat('%', #{commodityTitle}, '%')
AND order_table.actual_price = #{actualPrice}
AND pay_method = #{payMethod}
AND order_table.create_at BETWEEN #{startTime} AND #{endTime}
order by order_table.create_at desc

mapper.update:
update czb_wechat_commodity_order
set order_state = 3 -- 3，标识订单作废
where order_id = #{orderId,jdbcType=VARCHAR}
