mapper.select:
select ( fanyong_income + share_income + other_income - withdrawed ) balance
from czb_user_income_info
where info_id = #{infoId,jdbcType=VARCHAR}

mapper.update:
UPDATE czb_user_income_info
SET withdrawed = withdrawed + #{paymentAmount}
WHERE info_id = #{infoId}

mapper.select:
SELECT a.info_id infoId, a.amount, a.type, a.before_change_income beforeChangeIncome, a.remark, a.create_at createAt, b.user_name sourceUser, c.user_name gotUser FROM czb_income_log as a LEFT OUTER JOIN czb_user as b ON a.commission_source_user = b.user_id LEFT OUTER JOIN czb_user as c ON a.commission_got_user = c.user_id
a.info_id = #{infoId,jdbcType=VARCHAR}
and b.user_name like concat('%', #{sourceUser},'%')
and a.type = #{type,jdbcType=INTEGER}
and a.create_at between #{startTime} and #{endTime}
ORDER BY a.create_at DESC
