mapper.select:
select order_id orderId, user_name userName, phone phone, commodity_title commodityName, c.shop_name shopName, a.cost_price costPrice, third_order thirdOrder, order_state orderState, is_have_settled isHaveSettled FROM czb_wechat_commodity_order a LEFT JOIN czb_user AS b ON a.user_id = b.user_id LEFT JOIN czb_shop c ON a.shop_id = c.shop_id LEFT JOIN czb_wechat_commodity d ON a.commodity_id = d.commodity_id
a.is_have_settled = 0
and a.create_at between #{startTime} and #{endTime}
and a.shop_id = #{shopId}

select COUNT(cost_price)
from czb_wechat_commodity_order
WHERE shop_id = #{shopId};

mapper.update:
update czb_wechat_commodity_order
set is_have_settled = 1,settled_record_id = #{settledRecordId}
where order_id = #{orderId}
